contracts/FundManager.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";

import "@gnosis.pm/safe-contracts/contracts/proxies/GnosisSafeProxyFactory.sol";

import "@gnosis.pm/safe-contracts/contracts/GnosisSafe.sol";

import "./FundV1.sol";
import "./Registered.sol";
import "./TokenControl.sol";
import {Checks} from "./libraries/Checks.sol";

import "hardhat/console.sol";

contract FundManager is AccessControl, Registered {
    error NotAllowed();

    address public baseFund;
    address[] private funds;

    struct Fund {
        uint256 id;
        address at;
        string name;
        string focus;
        uint256 status;
    }

    constructor(address _registry, address _impl) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

        baseFund = _impl;

        _setRegistry(_registry);
    }

    // -----------------------------------------------------------------
    // PUBLIC API
    // -----------------------------------------------------------------

    /**
     * @dev         get a fund's name and focus
     * @param  _id  unique identifier of the fund
     * @return      fund name and focus
     */
    function getFundMeta(uint256 _id)
        public
        view
        returns (string memory, string memory)
    {
        return FundV1(funds[_id]).getMeta();
    }

    /**
     * @dev     get all fund addresses
     * @return  fund addresses
     */
    function getFunds() public view returns (address[] memory) {
        return funds;
    }

    /**
     * @dev                   get a fund's deposit address
     * @param  _id            unique identifier of the fund
     * @param  _tokenAddress  address of token tracker
     * @return                address of the fund
     */
    function getDepositAddressFor(uint256 _id, address _tokenAddress)
        public
        view
        returns (address)
    {
        return FundV1(funds[_id]).getDepositAddressFor(_tokenAddress);
    }

    /**
     * @dev          get a fund's deposit address
     * @param  _id   unique identifier of a fund
     * @return       address
     */
    function getFundAddress(uint256 _id) public view returns (address) {
        return funds[_id];
    }

    /**
     * @dev          get a list of allowed tokens for a fund
     * @param  _id   unique identifier of a fund
     * @return       list of addresses
     */
    function getAllowedTokens(uint256 _id)
        public
        view
        returns (address[] memory)
    {
        return FundV1(funds[_id]).getAllowedTokens();
    }

    /**
     * @dev                    check if token is allowed
     * @param  _id             unique identifier of a fund
     * @param  _tokenAddress   token address
     * @return                 boolean
     */
    function isTokenAllowed(uint256 _id, address _tokenAddress)
        public
        view
        returns (bool)
    {
        return FundV1(funds[_id]).isTokenAllowed(_tokenAddress);
    }

    // -----------------------------------------------------------------
    // ACCESS CONTROLLED
    // -----------------------------------------------------------------

    /**
     * @dev                    setup a new fund
     * @param  _name           name of the fund
     * @param  _focus          focus of the fund
     * @param  _description    description of the fund
     * @param  _safeAddress    address of underlying Gnosis Safe
     * @param  _allowedTokens  array of allowed token addresses
     * @param  _requestable    boolean indicating if fund is requestable
     * @param  _checks         a list of checks if fund is requestable
     */
    function createFund(
        string memory _name,
        string memory _focus,
        string memory _description,
        address _safeAddress,
        address[] memory _allowedTokens,
        bool _requestable,
        bytes32[2][] memory _checks,
        address[] memory _whitelist
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 index = funds.length;

        address cloneAddress = Clones.clone(baseFund);

        FundV1(cloneAddress).initialize(
            _name,
            _focus,
            _safeAddress,
            msg.sender,
            _allowedTokens,
            _requestable,
            _checks,
            _whitelist
        );

        funds.push(cloneAddress);

        emit FundCreated(index, cloneAddress, _name, _focus, _description);
    }

    /**
     * @dev                    setup a new fund
     * @param  _name           name of the fund
     * @param  _focus          focus of the fund
     * @param  _description    description of the fund
     * @param  _allowedTokens  array of allowed token addresses
     * @param  _owners         owners of the safe
       @param  _threshold      number of confirmations that the safe would need before a transaction
     */
    function createFundWithSafe(
        string memory _name,
        string memory _focus,
        string memory _description,
        address[] memory _allowedTokens,
        bool _requestable,
        bytes32[2][] memory _checks,
        address[] memory _whitelist,
        address[] memory _owners,
        uint256 _threshold
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_threshold > _owners.length) revert NotAllowed();
        uint256 index = funds.length;

        address cloneAddress = Clones.clone(baseFund);

        address _safeAddress = _setupSafe(_owners, _threshold);

        FundV1(cloneAddress).initialize(
            _name,
            _focus,
            _safeAddress,
            msg.sender,
            _allowedTokens,
            _requestable,
            _checks,
            _whitelist
        );

        funds.push(cloneAddress);

        emit FundCreated(index, cloneAddress, _name, _focus, _description);
    }

    /**
     * @dev           set implementation address
     * @param  _impl  address of underlying Gnosis Safe
     * @return        boolean indicating op. result
     */
    function setImplementation(address _impl)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
        returns (bool)
    {
        baseFund = _impl;
        return true;
    }

    // -----------------------------------------------------------------
    // INTERNAL API
    // -----------------------------------------------------------------

    function _setupSafe(address[] memory _owners, uint256 _threshold)
        internal
        returns (address)
    {
        bytes memory safeParams = _encodeSetup(
            _owners,
            _threshold,
            address(0),
            "0x",
            address(0),
            address(0),
            0,
            payable(address(0))
        );

        return
            address(
                GnosisSafeProxyFactory(_getAddress("GNOSIS_SAFE_PROXY_FACTORY"))
                    .createProxy(_getAddress("GNOSIS_SAFE"), safeParams)
            );
    }

    /**
     * @dev Encodes params and function for proxy creation
     * @param _owners List of Safe owners.
     * @param _threshold Number of required confirmations for a Safe transaction.
     * @param to Contract address for optional delegate call.
     * @param data Data payload for optional delegate call.
     * @param fallbackHandler Handler for fallback calls to this contract
     * @param paymentToken Token that should be used for the payment (0 is ETH)
     * @param payment Value that should be paid
     * @param paymentReceiver Address that should receive the payment (or 0 if tx.origin)
     * @return encoded Bytes representation of the params with setup signature
     */
    function _encodeSetup(
        address[] memory _owners,
        uint256 _threshold,
        address to,
        bytes memory data,
        address fallbackHandler,
        address paymentToken,
        uint256 payment,
        address payable paymentReceiver
    ) internal pure returns (bytes memory encoded) {
        encoded = abi.encodeWithSignature(
            "setup(address[],uint256,address,bytes,address,address,uint256,address)",
            _owners,
            _threshold,
            to,
            data,
            fallbackHandler,
            paymentToken,
            payment,
            paymentReceiver
        );
    }

    // -----------------------------------------------------------------
    // EVENTS
    // -----------------------------------------------------------------

    event FundCreated(
        uint256 indexed id,
        address indexed at,
        string name,
        string focus,
        string description
    );
}

contracts/FundV1.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";

import {Checks} from "./libraries/Checks.sol";
import {TokenControl} from "./TokenControl.sol";

import "hardhat/console.sol";

// Master Fund (v1) Contract
// FundManager create clones of this contract.
contract FundV1 is AccessControlEnumerable, TokenControl {
    using EnumerableSet for EnumerableSet.AddressSet;

    error NotAllowed();
    error NoZeroChecks();
    error Forbidden();

    bytes32 public constant AUDITOR_ROLE = keccak256("AUDITOR_ROLE");
    bytes32 public constant APPROVER_ROLE = keccak256("APPROVER_ROLE");
    bytes32 public constant FINALIZER_ROLE = keccak256("FINALIZER_ROLE");
    bytes32 public constant EXECUTOR_ROLE = keccak256("EXECUTOR_ROLE");

    Status public status;
    address private factory;
    address private safeAddress;
    bool public requestable;
    string public name;
    string public focus;

    bytes32[2][] public checks;
    EnumerableSet.AddressSet private whitelist;

    enum Status {
        Open,
        Paused,
        Closed
    }

    // called once by the factory at time of deployment
    // any subsequent calls will revert with Forbidden()
    function initialize(
        string memory _name,
        string memory _focus,
        address _safeAddress,
        address _owner,
        address[] memory _allowedTokens,
        bool _requestable,
        bytes32[2][] memory _checks,
        address[] memory _whitelist
    ) external {
        if (factory != address(0)) revert Forbidden();
        if (_requestable) {
            require(
                _checks.length > 0,
                "A set of initial checks are required for a requestable Fund."
            );
        }

        if (_whitelist.length > 0 || _checks.length > 0) {
            require(_requestable, "Fund must be set as requestable.");
        }

        _setupRole(DEFAULT_ADMIN_ROLE, _owner);

        factory = msg.sender;

        requestable = _requestable;
        name = _name;
        focus = _focus;
        safeAddress = _safeAddress;
        status = Status.Open;

        _setChecks(_checks);

        _batchSet(whitelist, _whitelist);
        _batchSet(allowedTokens, _allowedTokens);
    }

    // -----------------------------------------------------------------
    // PUBLIC API
    // -----------------------------------------------------------------

    /**
     * @dev                   get metadata for a fund
     * @return                metadata of the fund
     */
    function getMeta() external view returns (string memory, string memory) {
        return (name, focus);
    }

    /**
     * @dev                   get fund safe balances
     * @return                tuple of (tokenAddress[], balance[])
     */
    function getBalances()
        external
        view
        returns (address[] memory, uint256[] memory)
    {
        uint256 length = allowedTokens.length();

        address[] memory addresses = new address[](length);
        uint256[] memory balances = new uint256[](length);

        for (uint256 i = 0; i < length; i++) {
            addresses[i] = allowedTokens.at(i);
            balances[i] = IERC20(allowedTokens.at(i)).balanceOf(safeAddress);
        }

        return (addresses, balances);
    }

    /**
     * @dev                   get deposit address for a token
     * @param  _tokenAddress  tracker address of the token to deposit
                              (must be an allowed token)
     * @return                deposit address for a token
     */
    function getDepositAddressFor(address _tokenAddress)
        external
        view
        returns (address)
    {
        if (status != Status.Open) revert NotAllowed();
        if (!isTokenAllowed(_tokenAddress)) revert NotAllowed();
        if (safeAddress == address(0)) revert NotAllowed();
        return safeAddress;
    }

    /**
     * @dev                   check if a fund is open for donations
     * @return                boolean indicating status
     */
    function isOpen() external view returns (bool) {
        return status == Status.Open;
    }

    /**
     * @dev                   check if address is whitelisted
     * @return                boolean indicating status
     */
    function isWhitelisted(address _address) external view returns (bool) {
        if (whitelist.length() == 0) return true;
        return whitelist.contains(_address);
    }

    // -----------------------------------------------------------------
    // ACCESS CONTROLLED
    // -----------------------------------------------------------------

    /**
     * @dev             pause fund
     * @return          boolean indicating result of the operation
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) returns (bool) {
        if (status != Status.Open) revert NotAllowed();

        return _setStatus(Status.Paused);
    }

    /**
     * @dev             resume fund
     * @return          boolean indicating result of the operation
     */
    function resume() external onlyRole(DEFAULT_ADMIN_ROLE) returns (bool) {
        if (status != Status.Paused) revert NotAllowed();

        return _setStatus(Status.Open);
    }

    /**
     * @notice          this is final, a closed fund cannot be reopened
     * @dev             close fund
     * @return          boolean indicating result of the operation
     */
    function close() external onlyRole(DEFAULT_ADMIN_ROLE) returns (bool) {
        if (status == Status.Closed) revert NotAllowed();
        return _setStatus(Status.Closed);
    }

    // -----------------------------------------------------------------
    // INTERNAL
    // -----------------------------------------------------------------

    /**
     * @dev             set fund status
     * @param  _status  status to set
     * @return          boolean indicating result of the operation
     */
    function _setStatus(Status _status) internal returns (bool) {
        status = _status;

        emit StatusChange(uint256(_status));

        return true;
    }

    /**
     * @dev             batch set insertion
     * @param  _set     pointer to storage EnumerableSet
     * @param  _values  array of values to insert
     */
    function _batchSet(
        EnumerableSet.AddressSet storage _set,
        address[] memory _values
    ) internal {
        uint256 length = _values.length;
        uint256 i = 0;
        while (i < length) {
            _set.add(_values[i]);
            i++;
        }
    }

    // -----------------------------------------------------------------
    // EVENTS
    // -----------------------------------------------------------------

    event StatusChange(uint256 indexed id);

    function getCheck(uint256 _index) public view returns (bytes32[2] memory) {
        return checks[_index];
    }

    function allChecks() public view returns (bytes32[2][] memory) {
        return checks;
    }

    function addCheck(bytes32[2] memory _check)
        public
        onlyRole(AUDITOR_ROLE)
        returns (bool)
    {
        checks.push(_check);

        return true;
    }

    function removeCheck(uint256 _index)
        public
        onlyRole(AUDITOR_ROLE)
        returns (bool)
    {
        if (checks.length <= 1) revert NoZeroChecks();

        _shiftPop(checks, _index);
        return true;
    }

    function _setChecks(bytes32[2][] memory _initialChecks) internal {
        checks = _initialChecks;
    }

