contracts/libraries/HexStrings.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library HexStrings {
    bytes16 internal constant ALPHABET = "0123456789abcdef";

    /// @notice Converts a `uint256` to its ASCII `string` hexadecimal representation with fixed length.
    /// @dev Credit to Open Zeppelin under MIT license https://github.com/OpenZeppelin/openzeppelin-contracts/blob/243adff49ce1700e0ecb99fe522fb16cff1d1ddc/contracts/utils/Strings.sol#L55
    function toHexStringASCII(uint256 value, uint256 length)
        internal
        pure
        returns (string memory)
    {
        bytes memory buffer = new bytes(2 * length + 2);
        buffer[0] = "0";
        buffer[1] = "x";
        for (uint256 i = 2 * length + 1; i > 1; --i) {
            buffer[i] = ALPHABET[value & 0xf];
            value >>= 4;
        }
        require(value == 0, "Strings: hex length insufficient");
        return string(buffer);
    }

    function toHexStringASCIINoPrefix(uint256 value, uint256 length)
        internal
        pure
        returns (string memory)
    {
        bytes memory buffer = new bytes(2 * length);
        for (uint256 i = buffer.length; i > 0; i--) {
            buffer[i - 1] = ALPHABET[value & 0xf];
            value >>= 4;
        }
        return string(buffer);
    }
}

contracts/SVGComponents.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Strings.sol";

contract SVGComponents {
    using Strings for uint256;

    function colorToGridAnim(bytes[3] memory _colors)
        internal
        pure
        returns (bytes memory)
    {
        return
            abi.encodePacked(
                '<defs><linearGradient id="grad-anim" x1="0%" y1="0%" x2="100%" y2="100%">',
                stopTag(_colors, "0%"),
                stopTag([_colors[1], _colors[0], _colors[2]], "100%"),
                "</linearGradient></defs>"
            );
    }

    function stopTag(bytes[3] memory _colors, bytes memory _offset)
        internal
        pure
        returns (bytes memory)
    {
        return
            abi.encodePacked(
                '<stop offset="',
                _offset,
                '" stop-color="',
                _colors[0],
                '"><animate attributeName="stop-color" values="',
                _colors[0],
                ";",
                _colors[2],
                ";",
                _colors[1],
                ";",
                _colors[0],
                '" dur="7s" repeatCount="indefinite"/></stop>'
            );
    }

    function sideText(
        string memory _text,
        bytes memory _transform,
        bytes memory _anchor
    ) internal pure returns (bytes memory) {
        return
            abi.encodePacked(
                '<text class="small" transform="',
                _transform,
                '" style="text-anchor:',
                _anchor,
                '" fill="#FFF">',
                _text,
                "</text>"
            );
    }

    function titleStack(
        uint256 _x,
        uint256 _y,
        bytes memory _sub,
        bytes memory _title
    ) internal pure returns (bytes memory) {
        return
            abi.encodePacked(
                '<text class="title" transform="translate(',
                _x.toString(),
                ",",
                _y.toString(),
                ')"><tspan class="large" x="0">',
                _sub,
                '</tspan><tspan class="small alpha" x="0" dy="20">',
                _title,
                "</tspan></text>"
            );
    }
}

