//SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "./Registry.sol";

import "hardhat/console.sol";

contract Registered {
    Registry registry;

    constructor(address _registry) {
        registry = Registry(_registry);
    }

    // -----------------------------------------------------------------
    // INTERNAL API
    // -----------------------------------------------------------------

    /**
     * @dev           get and address from registry
     * @param  _name  registered name
     * @return        registered address
     */
    function getAddress(bytes32 _name) internal view returns (address) {
        return registry.get(_name);
    }
}

contracts/SOS.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

import "./FundManager.sol";
import "./FundV1.sol";
import "./NFTDescriptor.sol";
import "./Registered.sol";

import "hardhat/console.sol";

struct DonationRecord {
    uint256 fundId;
    uint256 amount;
    address tokenAddress;
}

contract SOS is AccessControl, ERC721, Registered {
    using Counters for Counters.Counter;

    Counters.Counter private tokenIds;
    mapping(uint256 => DonationRecord) public metadata;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    constructor(address _registry, address _minterAddress)
        ERC721("SOS Chain", "SOS")
        Registered(_registry)
    {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, _minterAddress);
    }

    // -----------------------------------------------------------------
    // PUBLIC API
    // -----------------------------------------------------------------

    function supportsInterface(bytes4 _interfaceId)
        public
        view
        override(ERC721, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(_interfaceId);
    }

    function tokenURI(uint256 _tokenId)
        public
        view
        override
        returns (string memory)
    {
        DonationRecord storage donation = metadata[_tokenId];

        address fundAddress = FundManager(getAddress("FUND_MANAGER"))
            .getFundAddress(donation.fundId);

        (string memory fundName, string memory fundFocus) = FundV1(fundAddress)
            .getMeta();

        NFTDescriptor descriptor = NFTDescriptor(getAddress("NFT_DESCRIPTOR"));

        address owner = ownerOf(_tokenId);

        return
            descriptor._constructTokenURI(
                _tokenId,
                owner,
                donation.amount,
                donation.tokenAddress,
                fundName,
                fundFocus
            );
    }

    // -----------------------------------------------------------------
    // ADMIN API
    // -----------------------------------------------------------------

    /**
     * @dev                   mint an nft
     * @param  _recipient     address of the recipient
     * @param  _fundId        id of the fund
     * @param  _amount        amount deposited
     * @param  _tokenAddress  tracked address of the deposited token
     * @return                id of the minted ERC721
     */
    function mint(
        address _recipient,
        uint256 _fundId,
        uint256 _amount,
        address _tokenAddress
    ) public onlyRole(MINTER_ROLE) returns (uint256) {
        tokenIds.increment();

        uint256 tokenId = tokenIds.current();
        _mint(_recipient, tokenId);

        metadata[tokenId] = DonationRecord({
            fundId: _fundId,
            amount: _amount,
            tokenAddress: _tokenAddress
        });

        return tokenId;
    }
}

